import React, { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import { Breadcrumb } from "../components/breadcrumb/breadcrumb";
import { Blog } from "../dummyData";

export const BlogDetails = () => {
  const { id } = useParams();
  const [item, setItem] = useState(null);
  useEffect(() => {
    let item = Blog.find((item) => item.id === parseInt(id));
    if (item) {
      setItem(item);
    }
  }, [id]);
  return (
    <section className="blog-details">
      <div className="container-blog-details">
        <div className="breadcrumb">
          <Breadcrumb
            active={item?.name}
            tag={
              <>
                <i className="fa fa-angle-right" aria-hidden="true"></i>
                <li className="breadcrumb-item active">
                  <Link to="/"> {item?.tags}</Link>
                </li>
              </>
            }
          />
        </div>
        <div className="box-blog-details">
          <div className="blog-details">
            <div className="content-blog-details">
              <article>
                <div className="box-img">
                  <img src={item.cover} alt="" />
                </div>
                <div className="box-details">
                <div className="box-date-time">
                  <div className="date">{item.date}</div>
                </div>
                <div className="box-desc"></div>
                </div>
              </article>
              <div className="comment">
                <div className="box-comment">
                  <div className="title">
                    <h3>Leave a Reply</h3>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};
