import React, { useState as UseState } from 'react'


export const ScrollButton = () => {
    const [visitble, setvisitble] = UseState(false);

    const toggleVisible = ()=>{
        const scrolled = document.documentElement.scrollTop;
        if(scrolled > 300){
            setvisitble(true);
        }
        else if(scrolled <=300){
            setvisitble(false);
        }
    }
    const scrollTop = ()=>{
        window.scrollTo({
            top: 0,
            behavior: 'smooth'
        })
    }
    window.addEventListener('scroll', toggleVisible);
  return (
    <button className="scroll-button-top" onClick={scrollTop}>
        <i className="fa fa-chevron-circle-up" style={{display: visitble ? 'flex' : 'none'}}></i>
    </button>
  )
}
